import React, { useState } from 'react';

function ContactForm() {
  const [text, setText] = useState('');

  const handleChange = (e) => {
    setText(e.target.value);
  };

  const handleUppercase = () => {
    setText(text.toUpperCase());
  };

  const handleLowercase = () => {
    setText(text.toLowerCase());
  };

  const handleSpeak = () => {
    if ('speechSynthesis' in window) {
      const utterance = new window.SpeechSynthesisUtterance(text);
      window.speechSynthesis.speak(utterance);
    } else {
      alert('Text-to-speech not supported in this browser.');
    }
  };

  return (
    <section className="contact-section">
      <h2 className="contact-heading">Text Utility</h2>
      <textarea
        value={text}
        onChange={handleChange}
        rows={6}
        placeholder="Enter your text here..."
        className="contact-form-textarea"
      />
      <div className="contact-form-actions">
        <button type="button" onClick={handleUppercase}>Uppercase</button>
        <button type="button" onClick={handleLowercase}>Lowercase</button>
        <button type="button" onClick={handleSpeak}>Speak</button>
      </div>
    </section>
  );
}

export default ContactForm;
