import React from 'react';

const services = [
  {
    title: 'Web Development',
    description: 'Modern, responsive websites and web apps.',
    img: 'https://images.unsplash.com/photo-1461749280684-dccba630e2f6?auto=format&fit=crop&w=400&q=80'
  },
  {
    title: 'App Development',
    description: 'Cross-platform mobile applications.',
    img: 'https://images.unsplash.com/photo-1519389950473-47ba0277781c?auto=format&fit=crop&w=400&q=80'
  },
  {
    title: 'Consulting',
    description: 'Expert advice for your digital strategy.',
    img: 'https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=400&q=80'
  }
];

const Services = () => (
  <section className="services-section" style={{ padding: '2rem 0', background: '#fff' }}>
    <h2 style={{ marginBottom: '2rem', color: '#1976d2' }}>Our Services</h2>
    <div style={{
      display: 'flex',
      justifyContent: 'center',
      gap: '2rem',
      flexWrap: 'wrap'
    }}>
      {services.map((service, idx) => (
        <div key={idx} style={{
          background: '#f7f9fa',
          borderRadius: '12px',
          boxShadow: '0 2px 8px rgba(0,0,0,0.07)',
          padding: '1.5rem',
          maxWidth: '300px',
          textAlign: 'center'
        }}>
          <img
            src={service.img}
            alt={service.title}
            style={{
              width: '100%',
              height: '160px',
              objectFit: 'cover',
              borderRadius: '8px',
              marginBottom: '1rem'
            }}
          />
          <h3 style={{ color: '#282c34', marginBottom: '0.5rem' }}>{service.title}</h3>
          <p style={{ color: '#555', fontSize: '1rem' }}>{service.description}</p>
        </div>
      ))}
    </div>
  </section>
);

export default Services;
