import React, { useState } from 'react';
import './ContactForm.css'; // Assuming the CSS file is named ContactForm.css

function ContactForm() {
  const [formData, setFormData] = useState({ name: '', email: '', phone: '', message: '' });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Thank you ${formData.name}, we will contact you shortly!`);
    setFormData({ name: '', email: '', phone: '', message: '' });
  };

  return (
    <section className="contact-section">
      <h2 className="contact-heading">Book an Appointment</h2>
      <form onSubmit={handleSubmit} className="contact-form">
        <input name="name" placeholder="Your Name" value={formData.name} onChange={handleChange} required />
        <input name="email" type="email" placeholder="Email Address" value={formData.email} onChange={handleChange} required />
        <input name="phone" type="tel" placeholder="Phone Number" value={formData.phone} onChange={handleChange} required />
        <textarea name="message" rows="4" placeholder="Describe your concern..." value={formData.message} onChange={handleChange} required />
        <button type="submit">Submit</button>
      </form>
    </section>
  );
}


export default ContactForm;
